INSERT INTO `atemi_lex_lexique` (`idLexique`, `idFamily`, `idSelecteur`, `idCaption`, `idProperty`, `name`, `icone`, `introtext`, `showmode`, `actif`, `ordre`, `nbmsgbypage`, `searchSeeAlsoMode`, `detailShowShortDef`, `detailShowDefinition`, `detailShowInterligne`, `detailShowButtons`, `buttonPosition`, `detailShowId`, `detailShowProperty`, `seealsomode`, `synchroniseterme`, `detailShowSeeAlso`, `detailSeeAlsoShowing`, `intlinksicon`, `iconolink`, `icolink`, `icoref`, `icosize`, `intlinkspopup`, `intlinksheight`, `intlinkswidth`, `nbtermebypage`, `detailSeeAlsoCache`, `detailGererVisit`, `detailShowVisit`, `showcategory`, `showIndexCategory`, `intautosubmit`, `anonpost`, `sendmail2webmaster`, `mail4webmaster`) VALUES (1, 1, 5, 0, 0, 'Japonais - Français', 'livre1.gif', 'Petit lexique à l\'usage des pratiquants, ce dictionnaire ne prétend pas vous apprendre le japonais, mais juste vous aider à mieux comprendre la terminologie utilisée dans les arts martiaux, notamment l\'aïkido, le iaïdo, le kobudo, ect.<br />Votre participation sera le bien venue pour le complèter, l\'améliorer ou le corriger. <p align="center"><a href="mailto:jjd@wakasensei.fr">J°J°D</a> </p>', 0, 1, 0, 10, 0, 1, 1, 1, 255, 2, 0, 0, 1, 0, 4, 0, 1, 6, 5, 3, 0, 0, 420, 350, 10, 0, 0, 0, 1, 0, 1, 1, 1, 'jjd@kiolo.com'),
(2, 1, 4, 0, 0, 'Français - Japonais', 'repertoire1.gif', '', 1, 0, 0, 10, 0, 1, 1, 0, 255, 2, 0, 0, 0, 0, 0, 0, 1, 6, 5, 3, 1, 1, 420, 350, 10, 1, 0, 0, 1, 0, 1, 1, 1, 'jjd@kiolo.com'),
(3, 4, 5, 2, 1, 'Librairie', 'livres3.gif', '<em>Ce dictionaire répertorie une liste non exhaustive de <strong>livres</strong> , de <strong>films</strong> de <strong>CDRom</strong> , ... concernant <strong>les arts martiaux et la culture japonaise</strong> . Si vous souhaitez y voir figurer des titres non répertoriés, envoyez moi une image de la couverture ou de la pochette ainsi q\'un résumé ou le texte au dos du livre.</em>', 0, 1, 0, 3, 0, 1, 7, 1, 255, 0, 0, 1, 0, 0, 0, 0, 1, 6, 5, 3, 1, 1, 420, 350, 10, 1, 0, 0, 1, 0, 1, 1, 1, 'jjd@kiolo.com'),
(4, 5, 5, 1, 2, 'Sommités', 'personne1.gif', 'Ce lexique répertorie les grandes personnalités qui ont influées dans le monde des arts martiaux. Si vous souhaitez y voir figurer quelqu\'un qui aurait été oublié, faites moi parvenir une petite biographie, et les apports qu\'il a eu dans et dans quelles disciplines.', 0, 1, 0, 10, 0, 1, 7, 0, 255, 2, 0, 0, 0, 0, 0, 0, 1, 6, 5, 3, 1, 1, 420, 350, 10, 1, 0, 0, 1, 0, 1, 1, 1, 'jjd@kiolo.com'),
(5, 5, 4, 3, 4, 'Club', 'dib_0058.gif', 'Ici sont répertoriés les clubs d\'aïkido, aïkibudo, kobudo et affinitaires.<br /><a href="mailto:jjd@wakasensei.fr">Tenez moi au courant de modification dans les coordonnées afin de maintenir cette liste à jour.</a> ', 0, 1, 99, 10, 3, 1, 5, 1, 255, 2, 0, 0, 0, 0, 4, 0, 0, 6, 5, 3, 0, 0, 420, 350, 8, 1, 1, 1, 1, 0, 1, 1, 1, 'jjd@wakasensei.fr');
